// new random value for field is_code
// Creation of random string
       $length=6; // length of random string
       $characters = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';  // legal characters
        $charactersLength = strlen($characters);
       $randomString = '';  //start with the empty string
       for ($i = 0; $i < $length; $i++) {
       $randomString .= $characters[random_int(0, $charactersLength - 1)];  // pick a random character
       }
$item->id_code = $randomString;
