// --- CUSTOM ENRICHMENT START getPlaceAltitude: Copy altitude from #__bookings_place ---
foreach ($items as $nr => &$item)
	{
    // Check if the item has the required foreign key (place_id)
	if (isset($item->place_id) && $item->place_id > 0) {
    //Executes the query to retrieve the altitude value from the #__bookings_place table.
    //SELECT altitude FROM #__bookings_place WHERE id = [place_id]
	$db = $this->getDbo();
    $query = $db->getQuery(true);

    // 1. SELECT the specific field 'altitude'
    $query->select($db->quoteName('altitude'));

    // 2. FROM the table '#__bookings_place'
    $query->from($db->quoteName('#__bookings_place'));

    // 3. WHERE clause to match the ID.
    // Use quoting for security, which is good practice.
    $query->where($db->quoteName('id') . ' = ' . $db->quote((int) $item->place_id));

    $db->setQuery($query);

    try {
       // loadResult() is used to fetch a single value (the altitude) from the first row.
        $altitude = $db->loadResult();
    	} catch (\Exception $e) {
        // Log the error (recommended practice)
        Factory::getApplication()->enqueueMessage('Database Error in Place Altitude Lookup: ' . $e->getMessage(), 'error');
        }
    // Assign the retrieved altitude to the new field name: 'place_altitude'
	// This overwrites the value loaded from the main table if one existed.
		$item->place_altitude = $altitude;
    } else {
            $item->place_altitude = null;
        }
	}
// --- CUSTOM ENRICHMENT END ---
