/**
 * Generates a random 6-digit code with a mix of numbers and capital letters.
 *
 * @returns {string} The 6-digit random alphanumeric code as a string.
 */
function generateAlphanumericCode() {
  // Define the set of characters to choose from: numbers 0-9 and capital letters A-Z.
  const characters = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  let result = '';
  const codeLength = 6;

  // Loop to generate a 6-character code.
  for (let i = 0; i < codeLength; i++) {
    // Generate a random index based on the length of the characters string.
    const randomIndex = Math.floor(Math.random() * characters.length);
    
    // Append the character at the random index to the result string.
    result += characters.charAt(randomIndex);
  }

  return result;
}

// --- Example Usage ---

// Get a new random alphanumeric code.
const code = generateAlphanumericCode();

// Log the code to the console.
console.log("Generated 6-digit alphanumeric code:", code);

