/* COMMENTO 2 */

$db = \Joomla\CMS\Factory::getContainer()->get('DatabaseDriver');

// Get the Publishers Group ID
$query = $db->getQuery(true)
    ->select($db->quoteName('id'))
    ->from($db->quoteName('#__usergroups'))
    ->where($db->quoteName('title') . ' = ' . $db->quote('Publisher'));
$publisher_group_id = $db->setQuery($query)->loadResult();

// Get the com_content Asset ID
$query = $db->getQuery(true)
    ->select($db->quoteName('id'))
    ->from($db->quoteName('#__assets'))
    ->where($db->quoteName('name') . ' = ' . $db->quote('com_content'));
$asset_id = $db->setQuery($query)->loadResult();

// 1. Get the existing rules for the component asset
$rules = Access::getRules($asset_id);

// 2. Define the new rules for the Publishers group (ID $publisher_group_id)
// We are setting 'core.edit.state' (publish/unpublish) to Allowed (1)
$rules->setRule(
    'core.edit.state',
    new Rule($publisher_group_id, 1),
    true // Overwrite existing rule
);

// You can add other rules:
// $rules->setRule('core.create', new Rule($publisher_group_id, 1), true);
// $rules->setRule('core.edit', new Rule($publisher_group_id, 1), true);

// 3. Save the modified rules back to the database
$result = Access::setRules($asset_id, $rules);

if ($result) {
    // Permission set successfully
} else {
    // Error saving permissions
}
